mapper.select:
SELECT botID FROM BotMapping

    WHERE userId = #{userId}


    WHERE companyId = #{companyId}

mapper.delete:
DELETE FROM BOT_MAPPING

    WHERE COMPANY_ID = #{companyId}


    WHERE USER_ID = #{sessId}

AND BOT_ID = #{host}

mapper.select:
SELECT COUNT(DISTINCT a.BOT_ID) AS count, b.MAX_CHATBOT AS userMax, c.MAX_CHATBOT AS compMax
FROM BOT_MAPPING a LEFT OUTER JOIN TN_USER b ON a.USER_ID = b.USER_ID
LEFT OUTER JOIN CM_COMPANY c ON a.COMPANY_ID = c.COMPANY_ID

    WHERE a.COMPANY_ID = #{companyId}


    WHERE a.USER_ID = #{sessId}
