mapper.select:
SELECT COUNT(A.id) AS COUNT , A.intent AS content FROM ChatbotLog.IntentLog A WHERE A.HOST = #{host} AND A.intent != '처음으로' AND #{startDate} <= DATE_FORMAT(createDate, '%Y-%m-%d') AND #{endDate} >= DATE_FORMAT(createDate, '%Y-%m-%d')
AND A.lang=#{lang}
GROUP BY A.intent ORDER BY COUNT(A.id) DESC LIMIT 10

SELECT COUNT(A.id) AS COUNT , A.intent AS content FROM ChatbotLog.IntentLog A WHERE A.HOST=#{host} AND A.intent != '처음으로' AND #{startDate} <= DATE_FORMAT(createDate, '%Y-%m-%d') AND #{endDate} >= DATE_FORMAT(createDate, '%Y-%m-%d')
AND A.lang=#{lang}
GROUP BY A.intent ORDER BY COUNT(A.id) DESC

		SELECT COUNT(A.id) AS COUNT
		     , A.utter AS content
		  FROM ChatbotLog.IntentLog A
		 WHERE A.intent = #{intent}
		   AND A.HOST = #{host}
		   AND #{startDate}  <=  DATE_FORMAT(createDate, '%Y-%m-%d')
		   AND #{endDate}  >=  DATE_FORMAT(createDate, '%Y-%m-%d')

           AND A.lang=#{lang}

		 GROUP BY A.utter
		 ORDER BY COUNT DESC

SELECT T.utter , T.intent , T.utterCnt AS utterCnt , T.intentCnt , COUNT(1) OVER(partition BY intent) AS mergeCnt FROM ( SELECT utter , intent , COUNT(1) AS utterCnt , SUM(COUNT(1)) OVER(partition BY intent) AS intentCnt FROM ChatbotLog.IntentLog WHERE 1=1 AND host = #{host} AND intent != '처음으로'
AND lang = #{lang}
AND #{startDate} <= DATE_FORMAT(createDate, '%Y-%m-%d') AND #{endDate} >= DATE_FORMAT(createDate, '%Y-%m-%d') GROUP BY intent,utter ) T ORDER BY T.intentCnt DESC ,T.intent DESC ,T.utter ASC

SELECT /*getObStatsChatList - 채팅상담 이력 리스트*/
ROW_NUMBER() OVER (ORDER BY startDate ASC) AS RNUM
ROW_NUMBER() OVER (ORDER BY startDate DESC) AS RNUM
, IFNULL(DATE_FORMAT(T.startDate, '%Y-%m-%d'), '-') AS daily
ROW_NUMBER() OVER (ORDER BY endDate ASC) AS RNUM
ROW_NUMBER() OVER (ORDER BY endDate DESC) AS RNUM
, IFNULL(DATE_FORMAT(T.endDate, '%Y-%m-%d'), '-') AS daily
, T.* FROM ( SELECT DISTINCT A.session , A.Id , A.host , A.channel , IFNULL(DATE_FORMAT(A.createdAt, '%Y-%m-%d %H:%i:%s'), '-') AS startDate , IFNULL(DATE_FORMAT(A.updatedAt, '%Y-%m-%d %H:%i:%s'), '-') AS endDate , IFNULL(B.NAME,'-') AS chatbotNm , (IFNULL(A.botCnt, 0) + IFNULL(A.consultantCnt,0)) AS totalChatCnt , IFNULL(A.botCnt, 0) AS botChatCnt , IFNULL(A.consultantCnt, 0) AS consultantCnt , IFNULL(A.consultantId, '-') AS consultant FROM ChatbotLog.SessionLog A LEFT OUTER JOIN Chatbot.Account B ON A.HOST = B.NO WHERE 1=1 AND A.host IN
#{botIdArr}
) AS T WHERE 1=1
AND T.consultant LIKE CONCAT('%', #{consultant} , '%')
AND T.totalChatCnt = #{totalChatCnt}
AND DATE_FORMAT(T.startDate, '%Y-%m-%d') >= #{fromDate}
AND DATE_FORMAT(T.startDate, '%Y-%m-%d %H:%i:%s') >= #{fromDate}
AND DATE_FORMAT(T.startDate, '%Y-%m-%d') <= #{toDate}
AND DATE_FORMAT(T.startDate, '%Y-%m-%d %H:%i:%s') <= #{toDate}
ORDER BY daily ASC, startDate ASC
ORDER BY daily DESC, startDate ASC
ORDER BY daily ASC, startDate DESC
ORDER BY daily DESC, startDate DESC
ORDER BY startDate ASC
ORDER BY startDate DESC
AND DATE_FORMAT(T.endDate, '%Y-%m-%d') >= #{fromDate}
AND DATE_FORMAT(T.endDate, '%Y-%m-%d %H:%i:%s') >= #{fromDate}
AND DATE_FORMAT(T.endDate, '%Y-%m-%d') <= #{toDate}
AND DATE_FORMAT(T.endDate, '%Y-%m-%d %H:%i:%s') <= #{toDate}
ORDER BY daily ASC, endDate ASC
ORDER BY daily DESC, endDate ASC
ORDER BY daily ASC, endDate DESC
ORDER BY daily DESC, endDate DESC
ORDER BY endDate ASC
ORDER BY endDate DESC
OFFSET #{offset, jdbcType=INTEGER} ROWS FETCH NEXT #{rowNum, jdbcType=INTEGER} ROWS ONLY

		SELECT COUNT(*) AS totalCount
		  FROM (
	 			 SELECT DISTINCT A.SESSION
		  			  , IFNULL(B.NAME,'-') AS chatbotNm
		  			  , (IFNULL(A.botCnt, 0) + IFNULL(A.consultantCnt,0)) AS totalChatCnt
		  			  , IFNULL(A.consultantId, '-') as consultant
		  			  , IFNULL(DATE_FORMAT(A.createdAt, '%Y-%m-%d %H:%i:%s'), '-') AS startDate
				  	  , IFNULL(DATE_FORMAT(A.updatedAt, '%Y-%m-%d %H:%i:%s'), '-') AS endDate
	  			   FROM ChatbotLog.SessionLog A
	  	LEFT OUTER JOIN Chatbot.Account B
	 	 			 ON A.HOST = B.NO
	 	 	   WHERE 1=1
 	  	   	   AND A.host in

		 	  		#{botIdArr}

	 		   ) T
	 		   WHERE 1=1

	      		 AND T.totalChatCnt = #{totalChatCnt}


	      		 AND T.consultant LIKE CONCAT('%', #{consultant} , '%')






									AND DATE_FORMAT(T.startDate, '%Y-%m-%d')  >=  #{fromDate}


									AND DATE_FORMAT(T.startDate, '%Y-%m-%d %H:%i:%s')  >=  #{fromDate}






									AND DATE_FORMAT(T.startDate, '%Y-%m-%d')  <=  #{toDate}


									AND DATE_FORMAT(T.startDate, '%Y-%m-%d %H:%i:%s')  <=  #{toDate}








									AND DATE_FORMAT(T.endDate, '%Y-%m-%d')  >=  #{fromDate}


									AND DATE_FORMAT(T.endDate, '%Y-%m-%d %H:%i:%s')  >=  #{fromDate}






									AND DATE_FORMAT(T.endDate, '%Y-%m-%d')  <=  #{toDate}


									AND DATE_FORMAT(T.endDate, '%Y-%m-%d %H:%i:%s')  <=  #{toDate}

